import React, { Fragment, useState } from 'react';

const Orders = () => {
  const [formData, setFormData] = useState({
    totalAmount: '',
    consumer: {
      givenNames: '',
      surname: '',
      email: ''
    }
  });
  const {
    totalAmount,
    consumer: { givenNames, surname, email }
  } = formData;

  const onChange = e => ({ ...formData, [e.target.name]: e.target.value });

  return (
    <Fragment>
      <h1 className='large text-primary'>Order</h1>
      <p className='lead'>
        <i className='fas fa-user' /> Create An Order
      </p>
      <form className='form' onSubmit={onSubmit}>
        <div className='form-group'>
          <input
            type='text'
            placeholder='totalAmount'
            name='totalAmount'
            value={totalAmount}
            onChange={e => onChange(e)}
            re
          />
        </div>
        <div className='form-group'>
          <input
            type='text'
            placeholder='givenNames'
            name='givenNames'
            value={consumer.givenNames}
            onChange={e => onChange(e)}
          />
        </div>
        <div className='form-group'>
          <input
            type='text'
            placeholder='surname'
            name='surname'
            value={consumer.surname}
            onChange={e => onChange(e)}
          />
        </div>
        <div className='form-group'>
          <input
            type='text'
            placeholder='email'
            name='email'
            value={consumer.email}
            onChange={e => onChange(e)}
          />
        </div>
        <input type='submit' className='btn btn-primary' value='Order' />
      </form>
    </Fragment>
  );
};

export default Orders;
