import React, { Fragment, useState } from 'react';
import axios from 'axios';

const Configuration = () => {
  const [configuration, setConfiguration] = useState(null);

  const getConfiguration = async e => {
    e.preventDefault();
    try {
      const config = {
        headers: {
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': '*'
        }
      };
      const res = await axios.get('/api/configurations/v2', config);
      setConfiguration(res.data);
      console.log(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <Fragment>
      <button variant='primary' onClick={e => getConfiguration(e)}>
        Get Configuration
      </button>
      {configuration && (
        <div>
          <p>{configuration.description}</p>
          <p>{configuration.locales}</p>
          <p>{configuration.maximumAmount.amount}</p>
          <p>{books.maximumAmount.amount}</p>
          <p>{books.minimumAmount.amount}</p>
          <p>{books.minimumAmount.currency}</p>
          <p>{books.numberOfPayments}</p>
          <p>{books.promotionUrl}</p>
          <p>{books.type}</p>
        </div>
      )}
    </Fragment>
  );
};

export default Configuration;
