import React, { Fragment, useState } from 'react';
import axios from 'axios';

const Orders = () => {
  const [formData, setFormData] = useState({
    totalAmount: '',
    givenNames: '',
    surname: '',
    email: ''
  });
  const { totalAmount, givenNames, surname, email } = formData;

  const onChange = e =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmitGetonfiguration = async e => {
    e.preventDefault();
    try {
      const config = {
        headers: {
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': '*'
        }
      };
      const res = await axios.get('/api/configurations/v2', config);
      console.log(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const onSubmit = async e => {
    e.preventDefault();
    const newOrder = {
      totalAmount,
      consumer: {
        givenNames,
        surname,
        email
      }
    };

    try {
      const config = {
        headers: {
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': '*'
        }
      };
      const body = JSON.stringify(newOrder);
      const res = await axios.post('api/orders/v2', body, config);
      console.log(res.data);
      return (
        <div>
          res.data
        </div>
      )
    } catch (err) {
      console.error(err);
    }

    console.log(newOrder);
  };
  return (
    <Fragment>
      <button variant='primary' onClick={e => onSubmitGetonfiguration(e)}>
        Get Configuration
      </button>
      <h1 className='large text-primary'>Order</h1>
      <p className='lead'>
        <i className='fas fa-user' /> Create An Order
      </p>
      <form className='form' onSubmit={e => onSubmit(e)}>
        <div className='form-group'>
          <input
            type='text'
            placeholder='totalAmount'
            name='totalAmount'
            value={totalAmount}
            onChange={e => onChange(e)}
            required
          />
        </div>
        <div className='form-group'>
          <input
            type='text'
            placeholder='givenNames'
            name='givenNames'
            value={givenNames}
            onChange={e => onChange(e)}
            required
          />
        </div>
        <div className='form-group'>
          <input
            type='text'
            placeholder='surname'
            name='surname'
            value={surname}
            onChange={e => onChange(e)}
            required
          />
        </div>
        <div className='form-group'>
          <input
            type='text'
            placeholder='email'
            name='email'
            value={email}
            onChange={e => onChange(e)}
            required
          />
        </div>
        <input type='submit' className='btn btn-primary' value='Order' />
      </form>
    </Fragment>
  );
};

export default Orders;
