import React, { Fragment, useState } from 'react';


const Orders = () => {

  const [formData, setFormData] = useState({
    totalAmount: '',
    consumer: {
      givenNames: '',
      surname: '',
      email: ''
    }
  });
  const { totalAmount, consumer: { givenNames, surname, email}} = formData;

  return (
    <Fragment>
      <h1 className='large text-primary'>Order</h1>
      <p className='lead'>
        <i className='fas fa-user' /> Create An Order
      </p>
      <form className='form' onSubmit={onSubmit}>
        <div className='form-group'>
          <input
            type='text'
            placeholder='totalAmount'
            name='totalAmount'
            value={totalAmount}
            onChange={onChange}
          />
        </div>
        <div className='form-group'>
          <input
            type='givenNames'
            placeholder='givenNames'
            name='givenNames'
            value={consumer.givenNames}
            onChange={onChange}
          />
        </div>
        <div className='form-group'>
          <input
            type='password'
            placeholder='Password'
            name='password'
            value={password}
            onChange={onChange}
          />
        </div>
        <div className='form-group'>
          <input
            type='password'
            placeholder='Confirm Password'
            name='password2'
            value={password2}
            onChange={onChange}
          />
        </div>
        <input type='submit' className='btn btn-primary' value='Register' />
      </form>
      <p className='my-1'>
        Already have an account? <Link to='/login'>Sign In</Link>
      </p>
    </Fragment>
  );
};

export default Orders;
