import React, { Fragment, useState } from 'react';
import axios from 'axios';

const Configuration = () => {
  const [formData, setFormData] = useState({
    totalAmount: '',
    givenNames: '',
    surname: '',
    email: ''
  });
  const { totalAmount, givenNames, surname, email } = formData;

  const onChange = e =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmitGetonfiguration = async e => {
    e.preventDefault();
    try {
      const config = {
        headers: {
          'Content-Type': 'application/json',
          'Access-Control-Allow-Origin': '*'
        }
      };
      const res = await axios.get('/api/configurations/v2', config);
      console.log(res.data);
      return (
        // <Fragment>
        //   <div>{res.data.description}</div>
        <p>hi dddddddd</p>
        // </Fragment>
      );
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <Fragment>
      <button variant='primary' onClick={e => onSubmitGetonfiguration(e)}>
        Get Configuration
      </button>
     </Fragment>
  );
};

export default Configuration;
